import React, { useState } from 'react';
import './Navbar.css'; // Estilo CSS para a barra de navegação
import logo from './imgs/logo_site.png';

function Navbar() {
  const [active, setActive] = useState("nav__menu");
  const [toggleIcon, setToggleIcon] = useState("nav__toggler");
  
  const navToggler = () => {
    setActive(active === 'nav__menu' ? 'nav__menu nav__active' : 'nav__menu');
    setToggleIcon(toggleIcon === 'nav__toggler' ? 'nav__toggler toggle' : 'nav__toggler');
  }

  return (
    <nav className='nav'>
      <div className="marca">
         <img src={logo} className='marca-image'/>
      </div>
      <a href='#' className='nav__brand'>DataVerse</a>
      <ul className={active}>
        <li className="nav__item"><a href="#" className="nav__link">Sobre</a></li>
        <li className="nav__item"><a href="#" className="nav__link">Developers</a></li>
        <li className="nav__item"><a href="#" className="nav__link">Estruturas</a></li>
        <li className="nav__item"><a href="#" className="nav__link">Contato</a></li>
      </ul>
      <div onClick={navToggler} className={toggleIcon}>
        <div className="line1"></div>
        <div className="line2"></div>
        <div className="line3"></div>
      </div>
    </nav>
  );
};

export default Navbar;
